import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';

function BasicExample() {
  const [task,setTask]=useState({name:'',status:false});
  const [tasks,setTasks]=useState([]); 
  const getTasks=()=>{
  return tasks.map((task)=><li className='list-group-item'>{task}</li>)
  }

const addTask=(t)=>{
  if(t){
    const newTask= [...tasks];
    newTask.push({name:t,status:false});
    setTasks(newTask);
    setTask({name:'',status:false});
  }
}




  return (
    <>
   <input className='form-control' type='text' placeholder='Enter the task' onChange={(e)=>{setTask(e.target.value)}}></input>
   <button className='btn btn-success w-100' onClick={()=>{addTask(task)}}>Add</button>
<ul className='list-group'> {getTasks()}</ul>

    </>
  );
}

export default BasicExample;
